INSERT INTO pages (slug, title, content, language, meta_title, meta_description) VALUES
('home', 'Home', '<h1>Welcome to Katy Murr</h1><p>Conference Interpreter, English Coach & Writer</p><p>Want to communicate better?</p>', 'en', 'Katy Murr - English Coaching, Interpreting & Writing Services', 'Professional English coaching, conference interpreting, and writing services. Fiction and non-fiction writing expertise.'),
('home', 'Accueil', '<h1>Bienvenue chez Katy Murr</h1><p>Interprète de conférence, Coach en anglais & Écrivaine</p><p>Vous voulez mieux communiquer ?</p>', 'fr', 'Katy Murr - Coaching en anglais, Interprétation & Services d''écriture', 'Services professionnels de coaching en anglais, interprétation de conférence et services d''écriture.'),
('about', 'About', '<h1>About Katy Murr</h1><p>Katy Murr is a professional English coach, conference interpreter, and writer with extensive experience in language services and creative writing. With a passion for communication and storytelling, Katy helps clients achieve their language and writing goals through personalized, professional services.</p><h2>Current Work</h2><p>Katy is currently involved in various projects including CAS (Context and Application Studies), the Rwanda project, and provides ongoing English coaching and conference interpreting services to clients worldwide.</p>', 'en', 'About - Katy Murr', 'Learn more about Katy Murr, professional English coach, interpreter, and writer.'),
('about', 'À propos', '<h1>À propos de Katy Murr</h1><p>Katy Murr est coach en anglais, interprète de conférence et écrivaine professionnelle avec une vaste expérience dans les services linguistiques et l''écriture créative. Avec une passion pour la communication et la narration, Katy aide les clients à atteindre leurs objectifs linguistiques et d''écriture grâce à des services personnalisés et professionnels.</p><h2>Travail actuel</h2><p>Katy est actuellement impliquée dans divers projets incluant CAS (Context and Application Studies), le projet Rwanda, et fournit des services de coaching en anglais et d''interprétation de conférence à des clients du monde entier.</p>', 'fr', 'À propos - Katy Murr', 'En savoir plus sur Katy Murr, coach en anglais, interprète et écrivaine professionnelle.');
